package JavaBasic;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Primitive data types : int,double,char,boolean

		// 1. integer-data type
		int i = 10;
		i = 20;
		int j = 30;
		System.out.println(j);
		int k = 40;
		System.out.println(j + k);
		int l = -1;
		System.out.println(l);
		int m = 0;

		// 2 . double
		double d = 12.33;
		double d1 = 34.44;
		System.out.println(d1);
		double d2 = 100;

		// 3 . character (char)-only single digit value,should be written in single
		// code.
		char c = 'Q';
		char c1 = 'A';
		char c2 = '1';
		char c3 = '5';

		// 4 . boolean
		boolean b1 = true;
		boolean b2 = false;

		// 5 . string : is a class ,not a data type,shpuld be written on double code.
		String s = "Hello world";
		String s1 = "Selenium";
		String s2 = "Hi this is java code";
		String s3 = "1000";
		String s4 = "12.33";
		System.out.println(s2);

	}

}
