package JavaBasic;

public final class LoopsConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 1. while loop
		int i = 0;
		while (i >= 10) {
			System.out.println(i);
			i = i + 1;
		}
		// 2. for loop
		for (int j = 1; j <= 1; j++) {
			System.out.println(j);
		}
		for (int k = 10; k >= -10; k--) {
			System.out.println(k);
		}

	}

}
